import { useEffect, useState } from 'react';
import Styles from './TODO.module.css';
import { dummy } from './dummy';
import axios from 'axios';

export function TODO(props) {

    const [newTodo, setNewTodo] = useState('')
    const [taskDescription, setTaskDescription] = useState('');
    const [todoData, setTodoData] = useState(dummy);
    const [editingTodoId, setEditingTodoId] = useState(null); 
    const [selectedTasks, setSelectedTasks] = useState([]);


    useEffect(() => {
        const fetchTodo = async () => {
            const apiData = await getTodo()
            setTodoData(apiData);
        }
        fetchTodo();
    }, [])

    const getTodo = async () => {
        const options = {
            method: "GET",
            url: `http://localhost:8000/api/todo`,
            headers: {
                accept: "application/json",
            }
        }
        try {
            const response = await axios.request(options)
            return response.data
        } catch (err) {
            console.log(err);
            return []; // return an empty array in case of error
        }
    }

    const addTodo = () => {
        const options = {
            method: "POST",
            url: `http://localhost:8000/api/todo`,
            headers: {
                accept: "application/json",
            },
            data: {
                title: newTodo,
                description: taskDescription
            }
        }
        axios
            .request(options)
            .then(function (response) {
                console.log(response.data)
                setTodoData(prevData => [...prevData, response.data.newTodo])
            })
            .catch((err) => {
                console.log(err)
            })
    }


    const editTodo = (id, newData) => {
        const options = {
            method: "PATCH",
            url: `http://localhost:8000/api/todo/${id}`,
            headers: {
                accept: "application/json",
            },
            data: newData
        };
        axios
            .request(options)
            .then(function (response) {
                console.log(response.data);
                // Delete the old todo from state after successful update
                setTodoData(prevData => {
                    const updatedData = prevData.map(todo => todo._id === id ? response.data : todo);
                    // Filter out the old todo if it's different from the updated one
                    if (!updatedData.find(todo => todo._id === id)) {
                        deleteTodo(id); // Delete old todo from database
                    }
                    return updatedData;
                });
                setEditingTodoId(null); // Exit editing mode after saving
            })
            .catch((err) => {
                console.log(err);
            });
    };

    const handleEdit = (id, currentTitle, currentDescription) => {
        // Start editing mode for the specified todo ID
        setEditingTodoId(id);
        setNewTodo(currentTitle); // Set current title to the edit input field
        setTaskDescription(currentDescription); // Set current description to the edit input field
    };

    const handleSave = (id) => {
        // Save edited todo
        updateTodo(id, { title: newTodo, description: taskDescription });
    };


    const deleteTodo = (id) => {
        const options = {
            method: "DELETE",
            url: `http://localhost:8000/api/todo/${id}`,
            headers: {
                accept: "application/json",
            }
        }
        axios
            .request(options)
            .then(function (response) {
                console.log(response.data)
                setTodoData(prevData => prevData.filter(todo => todo._id !== id))
            })
            .catch((err) => {
                console.log(err)
            })
    };

    const updateTodo = (id) => {
        const todoToUpdate = todoData.find(todo => todo._id === id)
        const options = {
            method: "PATCH",
            url: `http://localhost:8000/api/todo/${id}`,
            headers: {
                accept: "application/json",
            },
            data: {
                ...todoToUpdate,
                done: !todoToUpdate.done
            }
        }
        axios
            .request(options)
            .then(function (response) {
                console.log(response.data)
                setTodoData(prevData => prevData.map(todo => todo._id === id ? response.data : todo))
            })
            .catch((err) => {
                console.log(err)
            })
    };

    
    
   

    return (
        <div className={Styles.ancestorContainer}>
            <div className={Styles.headerContainer}>
                <h1>Tasks</h1>
                <span>
                    <input
                        className={Styles.todoInput}
                        type='text'
                        placeholder='Enter the task'
                        name='New Todo'
                        value={newTodo}
                        onChange={(event) => {
                            setNewTodo(event.target.value);
                        }}
                    />
                    <input
                        className={Styles.descriptionInput}
                        type='text'
                        placeholder='Enter the description for the task'
                        value={taskDescription}
                        onChange={(event) => {
                            setTaskDescription(event.target.value);
                        }}
                    />
                    <button
                        id='addButton'
                        name='add'
                        className={Styles.addButton}
                        onClick={() => {
                            addTodo();
                        }}
                    >
                        + New Todo
                    </button>
                    <button
                        className={Styles.deleteButton}
                        onClick={deleteSelectedTodos}
                        disabled={selectedTasks.length === 0} // Disable button if no tasks are selected
                    >
                        Delete Selected
                    </button>
                </span>
            </div>
            <div id='todoContainer' className={Styles.todoContainer}>
                   {(todoData.length > 0 ? (
                        todoData.map((entry,index) => (
                            <div key={entry._id} className={Styles.todo}>
                                <input
                                    type='checkbox'
                                    checked={selectedTasks.includes(entry._id)}
                                    onChange= {() => {
                                            updateTodo(entry._id)
                                        }
                                    }
                                />
                                {editingTodoId === entry._id ? (
                                    // Display edit inputs
                                    <>
                                        <button
                                            className={Styles.saveButton}
                                            onClick={() => handleSave(entry._id)}
                                        >
                                            Save
                                        </button>
                                    </>
                                ) : (
                                    // Display todo title and description
                                    <>
                                        <span>{entry.title}</span>
                                        <span>Description: ({entry.description})</span>
                                        <button
                                            className={Styles.editButton}
                                            onClick={() => handleEdit(entry._id, entry.title, entry.description)}
                                        >
                                            Edit
                                        </button>
                                        <button
                                            className={Styles.deleteButton}
                                            onClick={() => deleteTodo(entry._id)}
                                        >
                                            Delete
                                        </button>
                                    </>
                                )}
                            </div>
                        ))
                    ) : (
                        <p className={Styles.noTodoMessage}>No tasks available. Please add a new task.</p>
                    )

                )
                }
            </div>
        </div>
    )
};
